import requestsurl = 'http://localhost:8000/clients/'response = requests.get('http://localhost:8000/')# Данные нового клиентаdata = {    'phone_number': '71234567890',    'operator_code': 'ABC',    'tag': 'example_tag',    'timezone': 'GMT+3'}# Отправляем POST-запрос для создания нового клиентаresponse = requests.post(url, data=data)print(response.status_code)print(response.text)